import React, { useState } from "react";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
import TodoList from "./Todo/TodoList";

function App() {
	let state = getTodos();

	[
		{
			id: 1,
			completed: true,
			title: "Купить хлеб"
		},
		{
			id: 2,
			completed: false,
			title: "Купить масло"
		},
		{
			id: 3,
			completed: false,
			title: "Купить молоко"
		}
	];

	function getTodos() {
		fetch(process.env.REACT_APP_API + "TodoList")
		.then((response) => response.json())
		.then((data) => {
			return data
		});
	}
	const [todos, setTodos] = useState(state);

	function toggleTodo(id) {
		setTodos(
			todos.map((todo) => {
				if (todo.id === id) todo.completed = !todo.completed;
				return todo;
			})
		);
	}

	function deleteTodo(id) {
		setTodos(
			todos.filter((value) => {
				return value.id !== id;
			})
		);
	}

	function addTodo(title) {
		setTodos(
			todos.concat([{
				id: todos.length + 1,
				title: title,
				completed: false
			}])
		);
	}
	return (
		<Context.Provider value={{ deleteTodo }}>
			<div className="wrapper">
				<h1>Список дел</h1>
				<AddTodo onCreate={addTodo}></AddTodo>
				<TodoList todos={todos} onToggle={toggleTodo} key={Date.now()}/>
			</div>
		</Context.Provider>
	);
}

export default App;
