import React, { useState, useEffect } from "react";
import AddTodo from "./Todo/AddTodo";
import TodoList from "./Todo/TodoList";
import DeleteAll from "./Todo/DeleteAll";

function App() {
	function getTodos() {
		fetch("http://localhost:63281/api/TodoList")
			.then((response) => response.json())
			.then((data) => {
				setTodos(data);
			});
	}
	const [todos, setTodos] = useState([]);

	const [type, setType] = useState("")

	function toggleTodo(id) {
		setTodos(
			todos.map((todo) => {
				if (todo.id === id) todo.flagCompleted = !todo.flagCompleted;
				return todo;
			})
		);
	}

	const [value, setValue] = useState("");

	useEffect(() => {
		getTodos();
	}, [value]);
	function submitHandler(e) {
		e.preventDefault();
		fetch("http://localhost:63281/api/TodoList", {
			method: "POST",
			headers: {
				Accept: "application/json",
				"Content-Type": "application/json"
			},
			body: JSON.stringify({
				Title: value,
				FlagCompleted: false
			})
		});
		if (value.trim().length > 0) {
			setValue("");
		}
	}



	return (
		<div className="wrapper">
			<h1>Список дел</h1>
			<form onSubmit={submitHandler}>
				<input
					type={"text"}
					style={{ margin: "1rem 1rem 1rem 0rem " }}
					value={value}
					onChange={(e) => { setValue(e.target.value) }}
				></input>
				<button className="add-button" type="submit">
					Add
				</button>
			</form>
			<TodoList todos={todos} onToggle={toggleTodo} key={Date.now()} />
			<DeleteAll></DeleteAll>
		</div>
	);
}

export default App;
