import React, { useState, useEffect } from "react";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
import TodoList from "./Todo/TodoList";
import DeleteAll from "./Todo/DeleteAll";

function App() {
	function getTodos() {
		fetch("http://localhost:63281/api/TodoList")
			.then((response) => response.json())
			.then((data) => {
				setTodos(data);
			});
	}
	const [todos, setTodos] = useState([]);

	function toggleTodo(id) {
		setTodos(
			todos.map((todo) => {
				if (todo.id === id) todo.flagCompleted = !todo.flagCompleted;
				return todo;
			})
		);
	}

	function deleteTodo(id) {
		setTodos(
			todos.filter((value) => {
				return value.id !== id;
			})
		);
	}

	useEffect(() => {
		getTodos();
	}, [todos]);

	return (
		<Context.Provider value={{ deleteTodo }}>
			<div className="wrapper">
				<h1>Список дел</h1>
				<AddTodo></AddTodo>
				{
					1 < 0 ? <p>123</p> : null
				}

				<TodoList todos={todos} onToggle={toggleTodo} key={Date.now()} />
				<DeleteAll></DeleteAll>
			</div>
		</Context.Provider>
	);
}

export default App;
