import React, { useState } from "react";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
import TodoList from "./Todo/TodoList";

function App() {
	let state = []
	const [states, setStates] = useState([])
	getTodos();

	// [
	// 	{
	// 		id: 1,
	// 		completed: true,
	// 		title: "Купить хлеб"
	// 	},
	// 	{
	// 		id: 2,
	// 		completed: false,
	// 		title: "Купить масло"
	// 	},
	// 	{
	// 		id: 3,
	// 		completed: false,
	// 		title: "Купить молоко"
	// 	}
	// ];

	function getTodos() {
		fetch("http://localhost:63281/api/TodoList")
		.then((response) => response.json())
		.then((data) => {
			setStates(data)
		});
	}
	const [todos, setTodos] = useState(states);

	function toggleTodo(id) {
		setTodos(
			todos.map((todo) => {
				if (todo.id === id) todo.flagCompleted = !todo.flagCompleted;
				return todo;
			})
		);
	}

	function deleteTodo(id) {
		setTodos(
			todos.filter((value) => {
				return value.id !== id;
			})
		);
	}

	function addTodo(title) {
		setTodos(
			todos.concat([{
				id: todos.length + 1,
				title: title,
				flagCompleted: false
			}])
		);
	}
	return (
		<Context.Provider value={{ deleteTodo }}>
			<div className="wrapper">
				<h1>Список дел</h1>
				<AddTodo onCreate={addTodo}></AddTodo>
				<TodoList todos={todos} onToggle={toggleTodo} key={Date.now()}/>
			</div>
		</Context.Provider>
	);
}

export default App;
