import React, { useState } from "react";
import AddTodo from "./Todo/AddTodo";
import TodoList from "./Todo/TodoList";

function App() {
	let state = [
		{
			id: 1,
			completed: true,
			title: "Купить хлеб"
		},
		{
			id: 2,
			completed: false,
			title: "Купить масло"
		},
		{
			id: 3,
			completed: false,
			title: "Купить молоко"
		}
	];

	const [todos, setTodos] = useState(state);

	function toggleTodo(id) {
		setTodos(
			todos.map((todo) => {
				if (todo.id === id) todo.completed = !todo.completed;
				return todo;
			})
		);
	}

	function deleteTodo(id) {
		setTodos(
			todos.filter((value) => {
				return value.id !== id;
			})
		);
	}

	return (
		<div className="wrapper">
			<h1>Список дел</h1>
			<AddTodo></AddTodo>
			<TodoList todos={todos} onToggle={toggleTodo} deleteTodo={deleteTodo} />
		</div>
	);
}

export default App;
