import React, { useState } from "react";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
import TodoList from "./Todo/TodoList";

function App() {
	function getTodos() {
		fetch("http://localhost:63281/api/TodoList")
			.then((response) => response.json())
			.then((data) => {
				setTodos(data);
				console.log(data);
			});
	}
	const [todos, setTodos] = useState([]);

	function toggleTodo(id) {
		setTodos(
			todos.map((todo) => {
				if (todo.id === id) todo.flagCompleted = !todo.flagCompleted;
				return todo;
			})
		);
	}

	function deleteTodo(id) {
		setTodos(
			todos.filter((value) => {
				return value.id !== id;
			})
		);
	}

	function addTodo(title) {
		setTodos(
			fetch("http://localhost:63281/api/TodoList", {
				method: "POST",
				headers: {
					Accept: "application/json",
					"Content-Type": "application/json"
				},
				body: JSON.stringify({
					Title: title,
					FlagCompleted: false
				})
			}).then(
				(result) => {
					alert("Запись успешно добавлена");
				},

				(err) => {
					alert(err);
				}
			)
		);
		// useState(todos);
	}
	getTodos();

	return (
		<Context.Provider value={{ deleteTodo }}>
			<div className="wrapper">
				<h1>Список дел</h1>
				<AddTodo onCreate={addTodo} getTodo={getTodos}></AddTodo>
				<TodoList todos={todos} onToggle={toggleTodo} key={Date.now()} />
			</div>
		</Context.Provider>
	);
}

export default App;
